import React from 'react';
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import { ElementsConsumer} from "@stripe/react-stripe-js";
import { CardElement } from "@stripe/react-stripe-js";

const stripePromise = loadStripe('pk_live_51Io1VYEosDw1KI2wx07pjST1DfDOdJFBwKFtlCJ123DL0h7VScRUw0v3eOiHijyUemzzFymgWXQfnIre747CluLY00Mw6EUnm8');

const CARD_ELEMENT_OPTIONS = {
    iconStyle: "solid",
    hidePostalCode: true,
    style: {
      base: {
        iconColor: "rgb(240, 57, 122)",
        color: "rgb(240, 57, 122)",
        fontSize: "16px",
        fontFamily: '"Open Sans", sans-serif',
        fontSmoothing: "antialiased",
        "::placeholder": {
          color: "#CFD7DF"
        }
      },
      invalid: {
        color: "#e5424d",
        ":focus": {
          color: "#303238"
        }
      }
    }
};


function InjectedCheckoutForm() {
    return (
      <ElementsConsumer>
        {({ stripe, elements }) => (
          <CheckoutForm stripe={stripe} elements={elements} />
        )}
      </ElementsConsumer>
    );
}


class CheckoutForm extends React.Component {
    handleSubmit = async event => {
      event.preventDefault();
  
      handleSubmit = async event => {
        event.preventDefault();
      
        const { stripe, elements } = this.props;
        if (!stripe || !elements) {
          return;
        }
      
        const card = elements.getElement(CardElement);
        const result = await stripe.createToken(card);
        if (result.error) {
          console.log(result.error.message);
        } else {
          console.log(result.token);
          // pass the token to your backend API
        }
      };
    };
  
    render() {
      return (
        <div>
          <div class="product-info">
            <h3 className="product-title">Apple MacBook Pro</h3>
            <h4 className="product-price">$999</h4>
          </div>
          <form onSubmit={this.handleSubmit}>
            <CardSection />
            <button className="btn-pay">Buy Now</button>
          </form>
        </div>
      );
    }
}
  
function CardSection() {
    return <CardElement options={CARD_ELEMENT_OPTIONS} />;
}
function Stripe(){
    <Product/>
}

function Product (){
    return (
        <div className="App">
          <div className="product">
            <img
              src="https://images.pexels.com/photos/18105/pexels-photo.jpg?auto=compress"
              alt="laptop"
              style={{ width: "100%", height: "auto" }}
            />
            <div>
              {
                 <Elements stripe={stripePromise}>
                 <CheckoutForm />
                </Elements>
              }
            </div>
          </div>
        </div>
      );
}
export default Stripe;
