import React from 'react';
import ReactDom from 'react-dom';
import './index.css';
import App from './App';
import Stack from '@mui/material/Stack';
import CameraMaster from './Cameras';
import AegisMaster from './Aegis';
import CSSTransitionGroup from 'react-transition-group/CSSTransitionGroup';
import {Route,Switch, BrowserRouter as Router} from 'react-router-dom';
import AdminMaster from './App';
import 'remixicon/fonts/remixicon.css'
import SettingsMaster from './Settings';

function Main(){
  return(
    <div>
        <Links/>
    </div>
  )
}

function Links (){
  return(
  <div style={{padding: '0px'}}>
    <Stack direction="column" justifyContent="center" alignItems="flex-start">
    <div className="buttonParent">
    <p className="button"><a href="/App">Home</a></p>
    <p className="button"><a href="/Cameras">Cameras</a></p>
    <p className="button"><a href="/Aegis">Upgrade Aegis</a></p>
    <p className="button"><a href="/Settings">Settings</a></p>
    </div>   
    </Stack>
      <CSSTransitionGroup
        transitionName="example"
        transitionAppear={true}
        transitionAppearTimeout={500}
        transitionEnter={true}
        transitionLeave={true}>
        <Router>
          <Switch>
            <Route exact path="/" component={AdminMaster}/>
            <Route path="/App" component={AdminMaster}/>
            <Route path="/Cameras" component={CameraMaster}/>
            <Route path="/Aegis" component={AegisMaster}/>
            <Route path="/Settings" component={SettingsMaster}/>
          </Switch>
        </Router>
      </CSSTransitionGroup>

  </div>
  )
}
class ApiFetch extends React.Component{

  constructor(props){
    super(props)

    this.state = {
      heroes: []
    }
  } 

  componentDidMount(){
    var heroes = {
      method: 'GET',
      url: 'http://127.0.0.1:8000/heroes/',
      Allow: 'GET, POST, HEAD, OPTIONS',
      Vary: 'Accept',
      headers: {
          "Content-type": "application/json"
      }
    };
    axios.request(heroes)
    .then(response=>{
      this.setState({
          heroes: response.data
      })
      console.log(response.data)
    })
  }
  render(){
    if(!this.state.heroes[0]){
      return(
        <h1>Loading</h1>
      )
    }
    if(this.state.heroes[0]){
      var loadFile = function (event) {
          var image = document.getElementById("output");
          image.src = URL.createObjectURL(event.target.files[0]);
      };
      return(
       <div>
          <div id="autoScroll" className="buttonParent" style={{textAlign: 'center', width: '1020px', height: '650px', whiteSpace: 'nowrap', textOverflow: 'ellipsis', scrollBehavior: 'smooth', color: 'black'}}>
              <div class="profile-pic">
              <label class="-label" for="file">
              <span class="glyphicon glyphicon-camera"></span>
              <span>Change Image</span>
              </label>
              <input id="file" type="file" onchange="loadFile(event)" onChange={loadFile}/>
              <img src={cube} id="output" width="200" alt="defaultPic"/>
              </div><p style={{textAlign: 'center', fontStyle: 'italic', fontSize: '22px'}}>{this.state.heroes[0].name}</p>
              <p style={{textAlign: 'center', fontStyle: 'italic', fontSize: '22px', color: 'blue'}}>@{this.state.heroes[0].username}</p>
              <p style={{textAlign: 'center', fontStyle: 'italic', fontSize: '22px'}}>{this.state.heroes[0].companyName}</p>
              <p>Email Notifications</p>       
              <p>Manage In-App Notifications</p>
              <p>Aegis Recognition Documentation</p>         
              <p style={{color: '#121212', fontSize: '22px'}}>Contact May Realities</p>
              </div>
       </div>
      )
  }

}
}
export default App;
