import React from 'react';
import Stack from '@mui/material/Stack';
import './Cameras.css'




function CameraMaster(){
    return(
        <div style={{textAlign: 'center'}}>            
            <button className="cameraBtn" onClick={addCamera()}>Add Camera</button>
            <ControlCenter/>
        </div>
    )
}
function ControlCenter(){
    return(
        <div style={{textAlign: 'center', padding: '100px'}}>
            <Stack direction="row" spacing={2} justifyContent="center" alignItems="center">
            <p className="cameraScreen" id="screen">Front Entrance</p>
            <p className="cameraScreen" id="screen">Centre View</p>
            <p className="cameraScreen" id="screen">Centre View 2</p>
            <p className="cameraScreen" id="screen">Back Exit</p>
            </Stack>
        </div>
    )
}

function buffCenter(){
    return(
        <div>
            <div class="form-popup" id="myForm">
            <form action="/action_page.php" class="form-container">
            <h1>Login</h1>

        <label for="email"><b>Email</b></label>
        <input type="text" placeholder="Enter Email" name="email" required/>

        <label for="psw"><b>Password</b></label>
        <input type="password" placeholder="Enter Password" name="psw" required/>

        <button type="submit" class="btn">Login</button>
        <button type="button" class="btn cancel" onclick={addedCamera()}>Close</button>
        </form>
        </div>  
        </div>

    )
}
function addCamera(){
    document.getElementById("cameraForm").style.display = "block";
}
function addedCamera(){
    document.getElementById("myForm").style.display = "none";
}
export default CameraMaster;
