import React from 'react';
import ReactDom from 'react-dom';
import './index.css';
import App from './App';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import { styled } from '@mui/material/styles';
import HouseIcon from '@material-ui/icons/House';
import NotificationsActiveIcon from '@material-ui/icons/NotificationsActive';
import MenuIcon from '@material-ui/icons/Menu';
import Master from './AdminPanel';
import CameraMaster from './Cameras';
import AegisMaster from './Aegis';
import CSSTransitionGroup from 'react-transition-group/CSSTransitionGroup';
import {Route,Switch, BrowserRouter as Router} from 'react-router-dom';

function Main(){
  return(
    <div>
        <App />
        <Links/>
    </div>
  )
}
const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));


function Links (){
  return(
  <div>
    <Stack spacing={2} direction="column" justifyContent="center" alignItems="flex-start">
    <li style={{listStyleType: 'none', padding: '2px', paddingLeft: '5px', paddingBottom: '5px'}}><a href="/App" style={{fontSize: '18px'}}>Home</a></li>
      <li style={{listStyleType: 'none', padding: '2px', paddingLeft: '5px', paddingBottom: '5px'}}><a href="/Cameras" style={{fontSize: '18px'}} >Cameras</a></li>
      <li style={{listStyleType: 'none', padding: '2px', paddingLeft: '5px', paddingBottom: '5px'}}><a href="#" style={{fontSize: '18px'}}>Notifications</a></li>
      <li style={{listStyleType: 'none', padding: '2px', paddingLeft: '5px', paddingBottom: '5px'}}><a href="/Aegis" style={{fontSize: '18px'}}>Upgrade your Aegis</a></li>
      <li style={{listStyleType: 'none', padding: '2px', paddingLeft: '5px', paddingBottom: '5px'}}><a href="#" style={{fontSize: '18px'}}>Manage Organization</a></li>
      </Stack>
      <CSSTransitionGroup
        transitionName="example"
        transitionAppear={true}
        transitionAppearTimeout={500}
        transitionEnter={true}
        transitionLeave={true}>
        <Router>
          <Switch>
            <Route path="/Cameras" component={CameraMaster}/>
            <Route path="/Aegis" component={AegisMaster}/>
          </Switch>
        </Router>
      </CSSTransitionGroup>
  </div>
  )
}
export default App;
ReactDom.render(<Main/>,document.getElementById('root'))
