import {BarChart, Bar, LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip } from 'recharts';
import Stack from '@mui/material/Stack';

const dataInv = [
    {
      name: 'Mon',
      uv: 4000,
      pv: 2400,
      amt: 2400,
    },
    {
      name: 'Tues',
      uv: 3000,
      pv: 1398,
      amt: 2210,
    },
    {
      name: 'Wed',
      uv: 2000,
      pv: 9800,
      amt: 2290,
    },
    {
      name: 'Thurs',
      uv: 2780,
      pv: 3908,
      amt: 2000,
    },
    {
      name: 'Fri',
      uv: 1890,
      pv: 4800,
      amt: 2181,
    },
    {
      name: 'Sat',
      uv: 2390,
      pv: 3800,
      amt: 2500,
    },
    {
      name: 'Sun',
      uv: 3490,
      pv: 4300,
      amt: 2100,
    },
];


const dataCrime = [
    {
      name: 'Mon',
      uv: 4000,
      pv: 2400,
      amt: 2400,
    },
    {
      name: 'Tues',
      uv: 3000,
      pv: 1398,
      amt: 2210,
    },
    {
      name: 'Wed',
      uv: 2000,
      pv: 9800,
      amt: 2290,
    },
    {
      name: 'Thurs',
      uv: 2780,
      pv: 3908,
      amt: 2000,
    },
    {
      name: 'Fri',
      uv: 1890,
      pv: 4800,
      amt: 2181,
    },
    {
      name: 'Sat',
      uv: 2390,
      pv: 3800,
      amt: 2500,
    },
    {
      name: 'Sun',
      uv: 3490,
      pv: 4300,
      amt: 2100,
    },
];

function AdminMaster(){
    return( 
        <div>
            <GraphNotified/>      
        </div>
    )
}

function GraphNotified(){
    return(
        <div>
        <Stack spacing={4} direction="row" justifyContent="center" alignItems="center">
        <Stack spacing={40} direction="row" justifyContent="center" alignItems="center">
        <h1 style={{textAlign: 'center'}}>Estimated Lost Inventory</h1>
        </Stack>
        <Stack spacing={2} direction="row" justifyContent="center" alignItems="center">
        <BarChart width={550} height={250} data={dataInv} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
        <Line type="monotone" dataKey="uv" stroke="#8884d8" />
        <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        </BarChart>
        </Stack>
        
        <Stack spacing={40} direction="row" justifyContent="center" alignItems="center">
        <h1 style={{textAlign: 'center'}}>Crime Detection</h1>
        </Stack>
        <Stack spacing={2} direction="row" justifyContent="center" alignItems="center">
        <BarChart width={550} height={250} data={dataCrime} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
        <Line type="monotone" dataKey="uv" stroke="#8884d8" />
        <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <Bar dataKey="uv" fill="#82ca9d" maxBarSize="10" barSize="1"/>
        <YAxis />
        <Tooltip />
        </BarChart>
        </Stack>
        </Stack>
        
        <Stack spacing={2} direction="column" justifyContent="center" alignItems="center">
        <div id="autoScroll" className="buttonParent" style={{width: '1020px', height: '250px', overflow: 'scroll', whiteSpace: 'nowrap', textOverflow: 'ellipsis', scrollBehavior: 'smooth'}}>
            <p style={{textAlign: 'center', color: 'black'}}>Notification 1</p>
            <p style={{textAlign: 'center', color: 'black'}}>Notification 2</p>
            <p style={{textAlign: 'center', color: 'black'}}>Notification 3</p>
            <p style={{textAlign: 'center', color: 'black'}}>Notification 4</p>
            <p style={{textAlign: 'center', color: 'black'}}>Notification 5</p>
            <p style={{textAlign: 'center', color: 'black'}}>Notification 6</p>
        </div>
        </Stack>
        </div>
    )
}
window.setInterval(function() {
    var elem = document.getElementById('autoScroll');
    elem.scrollTop = elem.scrollHeight;
}, 777);
  
export default AdminMaster;
