import {Legend,LineChart, Line, Label, CartesianGrid, ResponsiveContainer, XAxis, YAxis, Tooltip } from 'recharts';
import Stack from '@mui/material/Stack';
import './AppStyle.css'
import notiFied from './notifications.json'

const dataCrime= [
    {
      name: 'Mon',
      uv: 5,
    },
    {
      name: 'Tues',
      uv: 0,
    },
    {
      name: 'Wed',
      uv: 4,
    },
    {
      name: 'Thurs',
      uv: 12,
    },
    {
      name: 'Fri',
      uv: 33,
    },
    {
      name: 'Sat',
      uv: 2,
    },
    {
      name: 'Sun',
      uv: 0,
    },
];


const dataInv = [
    {
      name: 'Mon',
      LI: "12"
    },
    {
      name: 'Tues',
      LI: "1"
    },
    {
      name: 'Wed',
      LI: "5"
    },
    {
      name: 'Thurs',
      LI: "2"
    },
    {
      name: 'Fri',
      LI: "7"
    },
    {
      name: 'Sat',
      LI: "0"
    },
    {
      name: 'Sun',
      LI: "1"
    },
];




function AdminMaster(){
    return( 
        <div>
            <h1>Notifications</h1>
            <GraphNotified/>      
        </div>
    )
}

function GraphNotified(){
    return(
        <div>
        <Stack spacing={2} direction="row" justifyContent="center" alignItems="center">

        <LineChart width={600} height={300} data={dataInv} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
          <Line type="monotone" dataKey="LI" stroke="#8884d8" name="Estimated Inventory Loss"/>
          <CartesianGrid stroke="#ccc" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
        </LineChart>
   
      

       
        <LineChart width={600} height={300} data={dataCrime} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
          <Line type="monotone" dataKey="uv" stroke="#8884d8" name="Estimated Crimes Detected"/>
          <CartesianGrid stroke="#ccc" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
        </LineChart>
  

        </Stack>
        
        <Stack spacing={2} direction="column" justifyContent="center" alignItems="center">
        <div id="autoScroll" className="buttonParent" style={{width: '1020px', height: '250px', overflowX: 'auto', whiteSpace: 'nowrap', textOverflow: 'ellipsis', scrollBehavior: 'smooth', color: 'black'}}>
            {notiFied.map((postDetail, index)=>{
                return<p>{postDetail.notification}</p>
            })}
        </div>
        </Stack>
        </div>
    )
}
//var elem = document.getElementById('autoScroll');
//elem.scrollTop = elem.scrollHeight;
  
export default AdminMaster;
