import React from 'react';
import Stack from '@mui/material/Stack';
import './Cameras.css';
import Popup from './components/Popup.js'
import 'reactjs-popup/dist/index.css';
import {useState} from 'react';
import Add from './Images/add.svg'


function CameraMaster(){
    const [buttonPopup, setButtonPopup] = useState(false)
    return(
        <div style={{textAlign: 'center'}}>
            <Stack direction="row" spacing={2} justifyContent="center" alignItems="center">
            <button className="close-btn" onClick={() => setButtonPopup(true)}>Add Camera</button>
            <Popup trigger={buttonPopup} setTrigger={setButtonPopup}>
                <p style={{textAlign: 'center', backgroundColor: '#121212', color: 'white'}}>Enter Camera IP Address</p>
                <input type="text" style={{textAlign: 'center'}}></input>
                <div style={{backgroundColor: '#3ce06d', maxWidth:'0px'}}>
                <Stack direction="row" spacing={2} justifyContent="center" alignItems="center">
                <button className="add-btn" style={{textAlign: 'center'}}><img src={Add} alt="Add" style={{backgroundColor: '#3ce06d'}}></img></button>
                </Stack>
                </div>
            </Popup>
            </Stack>
            <ControlCenter/>
        </div>
    )
}
function ControlCenter(){
    return(
        <div style={{textAlign: 'center', padding: '100px'}}>
            <Stack direction="row" spacing={2} justifyContent="center" alignItems="center">
            <p className="cameraScreen" id="screen">Front Entrance</p>
            <p className="cameraScreen" id="screen">Centre View</p>
            <p className="cameraScreen" id="screen">Centre View 2</p>
            <p className="cameraScreen" id="screen">Back Exit</p>
            </Stack>
        </div>
    )
}

export default CameraMaster;
